export default {
  name: 'AboutMe',
  props: {},
  methods: {
    facemain: function facemain() {
      var face1 = document.getElementById('face1');
      var face2 = document.getElementById('face2');
      var face3 = document.getElementById('face3');
      var face4 = document.getElementById('face4');
      var face5 = document.getElementById('face5');

      var onScrollHandler = function onScrollHandler() {
        var myimage = document.getElementById('face1');
        var scrollTop = document.documentElement.scrollTop || document.body.scrollTop;

        function changebg() {
          if (scrollTop < myimage.offsetTop - 200) {
            face1.style.opacity = 1;
            face2.style.opacity = 0;
            face3.style.opacity = 0;
            face4.style.opacity = 0;
            face5.style.opacity = 0;
          }

          if (scrollTop > myimage.offsetTop - 200) {
            face1.style.opacity = 0;
            face2.style.opacity = 1;
            face3.style.opacity = 0;
            face4.style.opacity = 0;
            face5.style.opacity = 0;
          }

          if (scrollTop > myimage.offsetTop - 100) {
            face1.style.opacity = 0;
            face2.style.opacity = 0;
            face3.style.opacity = 1;
            face4.style.opacity = 0;
            face5.style.opacity = 0;
          }

          if (scrollTop > myimage.offsetTop) {
            face1.style.opacity = 0;
            face2.style.opacity = 0;
            face3.style.opacity = 0;
            face4.style.opacity = 1;
            face5.style.opacity = 0;
          }

          if (scrollTop > myimage.offsetTop + 100) {
            face1.style.opacity = 0;
            face2.style.opacity = 0;
            face3.style.opacity = 0;
            face4.style.opacity = 0;
            face5.style.opacity = 1;
          }
        }

        changebg();
      };

      window.addEventListener("scroll", onScrollHandler);
    }
  },
  mounted: function mounted() {
    this.facemain();
  }
};
