export default {
  name: 'AboutMe',
  props: {},
  methods: {
    facemain: function facemain() {
      var face1 = document.getElementById('face1');
      var face2 = document.getElementById('face2');
      var face3 = document.getElementById('face3');
      var face4 = document.getElementById('face4');
      var face5 = document.getElementById('face5');
      var scaleimage = document.getElementsByClassName('scaleimage');
      var fadeel = document.getElementsByClassName('fadeel');

      var onScrollHandler = function onScrollHandler() {
        var myimage = document.getElementById('face1');
        var scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
        var hi = window.innerHeight;
        /*
        let contDiv = document.getElementsByClassName('contact')
        let container = document.getElementById('container')  
        function blurEl(){
          if (scrollTop>hi&& scrollTop<(contDiv[0].offsetTop-contDiv[0].clientHeight/2)){
              container.style.webkitFilter = "blur(10px)"
          } else {container.style.webkitFilter = "blur(0px)"}
        }
        blurEl()
        */

        function scaleel() {
          for (var i = 0; i < scaleimage.length; i++) {
            var pos = scaleimage[i].getBoundingClientRect();
            var scale = 1 - (pos.height / 2 + pos.top) * 0.001;
            scaleimage[i].style.transition = "all 1s";

            if (scale > 0.15 && scale < 0.85) {
              scaleimage[i].style.clipPath = 'circle(50% at 50% 50%)';
            } else {
              scaleimage[i].style.clipPath = 'circle(0% at 50% 50%)';
            }
          }
        }

        function fadeelfunc() {
          for (var i = 0; i < fadeel.length; i++) {
            var pos = fadeel[i].getBoundingClientRect();

            if ((pos.top > hi * 0.2 || pos.bottom > hi * 0.2) && (pos.top < hi * 0.8 || pos.bottom < hi * 0.8)) {
              fadeel[i].style.opacity = '1';
            } else {
              fadeel[i].style.opacity = '0';
            }
          }
        }

        function changebg() {
          if (myimage) {
            if (scrollTop < myimage.offsetTop - 200) {
              face1.style.opacity = 1;
              face2.style.opacity = 0;
              face3.style.opacity = 0;
              face4.style.opacity = 0;
              face5.style.opacity = 0;
            }

            if (scrollTop > myimage.offsetTop - 200) {
              face1.style.opacity = 0;
              face2.style.opacity = 1;
              face3.style.opacity = 0;
              face4.style.opacity = 0;
              face5.style.opacity = 0;
            }

            if (scrollTop > myimage.offsetTop - 100) {
              face1.style.opacity = 0;
              face2.style.opacity = 0;
              face3.style.opacity = 1;
              face4.style.opacity = 0;
              face5.style.opacity = 0;
            }

            if (scrollTop > myimage.offsetTop) {
              face1.style.opacity = 0;
              face2.style.opacity = 0;
              face3.style.opacity = 0;
              face4.style.opacity = 1;
              face5.style.opacity = 0;
            }

            if (scrollTop > myimage.offsetTop + 100) {
              face1.style.opacity = 0;
              face2.style.opacity = 0;
              face3.style.opacity = 0;
              face4.style.opacity = 0;
              face5.style.opacity = 1;
            }
          }
        }

        changebg();
        scaleel();
        fadeelfunc();
      };

      window.addEventListener("scroll", onScrollHandler);
    }
  },
  mounted: function mounted() {
    this.facemain();
  }
};
