var fadeel = document.getElementsByClassName('fadeel');

function fadeelfunc() {
  for (var i = 0; i < fadeel.length; i++) {
    var pos = fadeel[i].getBoundingClientRect();

    if (pos.top < window.innerHeight / 2 && pos.bottom > window.innerHeight / 2) {
      fadeel[i].style.opacity = '1';
    } else {
      fadeel[i].style.opacity = '0';
    }
  }
}

window.addEventListener("scroll", fadeelfunc);
